import React, { Component } from "react";
import {
  View,
  Text,
  StyleSheet,
  FlatList,
  TouchableOpacity,
  ActivityIndicator,
  AsyncStorage,
  ScrollView,
  Image,
  ImageBackground,
  Alert
} from "react-native";
import { Header } from "react-native-elements";
import CompletedServicesCard from "./CompleteServicesCard";
import CustomHeader from "../Header/CustomHeader";
import { NavigationEvents } from 'react-navigation';
import AntIcon from "react-native-vector-icons/AntDesign";
import * as CONSTANT from '../Constants/Constant';
const Entities = require("html-entities").Html5Entities;
const entities = new Entities();
class CompletedServices extends Component {
  constructor() {
    super();
    this.onEndReachedCalledDuringMomentum = true;
    this.state = {
    data: [],
    isLoading: true,
    Toataldata: "",
    page: 1,
    fetching_from_server: false,
    fetchCompletedServices:[],
    storedValue: "",
    storedType: "",
    profileImg: "",
    type: "",
    id: "",
    permissionChat:"",
    showAlert: false
  };
  this.offset = 1;
}
  componentWillMount() {
    this.getUser();
    this.fetchCompletedService();
  }
  getUser = async () => {
    try {
      const permissionChat = await AsyncStorage.getItem("chatPermission");
      const storedValue = await AsyncStorage.getItem("full_name");
      const storedType = await AsyncStorage.getItem("user_type");
      const profileImg = await AsyncStorage.getItem("profile_img");
      const type = await AsyncStorage.getItem("profileType");
      const id = await AsyncStorage.getItem("projectUid");

      //  console.log(storedValue ,storedType, profileImg  ,type , id);
      if (storedValue !== null) {
        this.setState({ storedValue });
      } else {
        // alert('something wrong')
      }
      if (storedType !== null) {
        this.setState({ storedType });
      } else {
        //  alert('something wrong')
      }
      if (profileImg !== null) {
        this.setState({ profileImg });
      } else {
        //  alert('something wrong')
      }
      if (type !== null) {
        this.setState({ type });
      } else {
        //  alert('something wrong')
      }
      if (id !== null) {
        this.setState({ id });
      } else {
        //  alert('something wrong')
      }
      if (permissionChat !== null) {
        this.setState({ permissionChat });
      } else {
        //  alert('something wrong')
      }
    } catch (error) {
      // alert(error)
    }
  };
  fetchCompletedService = async () => {
    const Pid = await AsyncStorage.getItem("projectUid");
    const storedType = await AsyncStorage.getItem("user_type");
    if(storedType == "freelancer"){
      const response = await fetch(
        CONSTANT.BaseUrl + "dashboard/get_services_by_type?type=completed&user_id=" + Pid
      );
      const json = await response.json();
      if (Array.isArray(json) && json[0] && json[0].type && json[0].type === 'error') {
        this.setState({ fetchCompletedServices: [] }); // empty data set 
      } else {
        this.setState({ fetchCompletedServices: json  , isLoading:false},
          );
      }
    }else{
      const response = await fetch(
        CONSTANT.BaseUrl + "dashboard/get_employer_services?type=completed&user_id=" + Pid
      );
      const json = await response.json();
      if (Array.isArray(json) && json[0] && json[0].type && json[0].type === 'error') {
        this.setState({ fetchCompletedServices: [] }); // empty data set 
      } else {
        this.setState({ fetchCompletedServices: json  , isLoading:false},
          );
      }
    }
    
  };
 
  renderFooter() {
    return (
      //Footer View with Load More button
      <View>
        {this.state.Toataldata.toString() != this.state.data.length ? (
          <View style={styles.footer}>
            <TouchableOpacity
              onPress={this.loadMoreData}
              style={styles.loadMoreBtn}
            >
              <Text style={styles.btnText}>Load More</Text>
              {this.state.fetching_from_server ? (
                <ActivityIndicator color="white" style={{ marginLeft: 8 }} />
              ) : null}
            </TouchableOpacity>
          </View>
        ) : null}
      </View>
    );
  }
  render() {
    const { isLoading } = this.state;
    const { storedValue, storedType, profileImg, permissionChat ,showAlert } = this.state;
    return (
      <View style={styles.container}>
       
       <View
          style={{
            height: 60,
            paddingLeft: 15,
            paddingRight: 15,
            width: '100%',
            backgroundColor: CONSTANT.statusBarColor,
            flexDirection: 'row',
            shadowOffset: {width: 0, height: 2},
            shadowColor: '#000',
            shadowOpacity: 0.2,
            shadowRadius: 2,
            elevation: 10,
          }}>
          <TouchableOpacity
            onPress={() => this.props.navigation.goBack(null)}
            style={{
              flexDirection: 'column',
              width: '20%',
              display: 'flex',
              alignContent: 'center',
              alignSelf: 'center',
              justifyContent: 'center',
            }}>
            <AntIcon name="back" size={25} color={'#fff'} />
          </TouchableOpacity>
          <View
            style={{
              flexDirection: 'column',
              width: '60%',
              display: 'flex',
              alignContent: 'center',
              alignSelf: 'center',
              justifyContent: 'center',
            }}>
            <View
              style={{
                flexDirection: 'row',
                display: 'flex',
                alignSelf: 'center',
              }}>
              <Text
                style={{
                  fontSize: 18,
                  fontWeight: '500',
                  color: '#fff',
                  height: 30,
                  marginTop: 9,
                }}>
                Completed Services
              </Text>
            </View>
          </View>
        </View>
        {isLoading && (
          <View style={{ justifyContent: "center", height: "100%" }}>
            <ActivityIndicator
              size="small"
              color={CONSTANT.primaryColor}
              style={{
                height: 30,
                width: 30,
                borderRadius: 60,
                alignContent: "center",
                alignSelf: "center",
                justifyContent: "center",
                backgroundColor: "#fff",
                elevation: 5
              }}
            />
          </View>
        )}
        {
          this.state.data != "" &&
          <View>
            <Text style={{fontSize:18 , margin:10 , fontWeight:'700'}}>{this.state.data[0].count_totals} Employers found:</Text>
          </View>
        }
        {
            this.state.fetchCompletedServices.length >= 1 ?
            <FlatList
            data={this.state.fetchCompletedServices}
            keyExtractor={(x, i) => i.toString()}
            renderItem={({ item , index }) => (
              <TouchableOpacity style={{
                  backgroundColor: '#fff', flexDirection: 'column', borderRadius: 4, overflow: 'hidden', flex: 1, flexDirection: 'column', margin: 2,
                  marginHorizontal:10
                }}
                  activeOpacity={0.9}
                  onPress={()=> this.props.navigation.navigate("CompleteServicesDetail", 
                  {data : this.state.fetchCompletedServices[index]}
                  )}
                >
                  {
                    storedType == "freelancer" ?
                    <CompletedServicesCard
                    employer_avatar={{ uri: `${item.employer_avatar != "" ? item.employer_avatar : 'data:image/jpeg;base64,'}` }}
                    employer_title={`${entities.decode(item.employer_title)}`}
                    employertagline={`${entities.decode(item.employertagline)}`}
                    service_title={`${entities.decode(item.service_title)}`}
                    price={`${entities.decode(item.price)}`}
                    featured_img={{ uri: `${item.featured_img != "" ? item.featured_img : 'data:image/jpeg;base64,'}` }}
                    service_ratings={`${entities.decode(item.service_ratings)}`}
                    is_featured={`${entities.decode(item.is_featured)}`}
                    />
                    :
                    <CompletedServicesCard
                    employer_avatar={{ uri: `${item.freelancer_avatar != "" ? item.freelancer_avatar : 'data:image/jpeg;base64,'}` }}
                    employer_title={`${entities.decode(item.freelancer_title)}`}
                    employertagline={`${entities.decode(item.freelancertagline)}`}
                    service_title={`${entities.decode(item.service_title)}`}
                    price={`${entities.decode(item.price)}`}
                    featured_img={{ uri: `${item.featured_img != "" ? item.featured_img : 'data:image/jpeg;base64,'}` }}
                    service_ratings={`${entities.decode(item.service_ratings)}`}
                    is_featured={`${entities.decode(item.is_featured)}`}
                    />

                  }
                
              </TouchableOpacity>
            )}
          //   ListFooterComponent={this.renderFooter.bind(this)}
          />
          :
          <View style={styles.MainScrollArea}>
          <Image
            resizeMode={'contain'}
            style={styles.MainScrollImageStyle}
            source={require('../Images/nodata.png')}
          />
        </View>
        }
        
      </View>
    );
  }
}
export default CompletedServices;
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#ffffff"
  },
  footer: {
    padding: 10,
    justifyContent: "center",
    alignItems: "center",
    flexDirection: "row"
  },
  loadMoreBtn: {
    paddingTop: 10,
    paddingBottom: 10,
    paddingLeft: 20,
    paddingRight: 20,
    backgroundColor: CONSTANT.primaryColor,
    borderRadius: 4,
    flexDirection: "row",
    justifyContent: "center",
    alignItems: "center"
  },
  btnText: {
    color: "white",
    fontSize: 15,
    textAlign: "center",
    fontFamily:CONSTANT.PoppinsMedium,
  },
  MainScrollArea: {
    flex: 1,
    marginTop: '40%',
    alignContent: 'center',
    height: '100%',
    width: '100%',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
  },
  MainScrollImageStyle: {
    width: 250,
    height: 250,
  },
  MainScrollOopsText: {
    fontSize: 25,
    fontWeight: '700',
    marginVertical: 10,
  },
  MainScrollNoDataText: {
    fontSize: 17,
    fontWeight: '700',
  },
});
